    private void InicioDatas(ResultSet BodegasInicio) throws SQLException{
        int[] Colum = {1,2,3,4,5,6}; 
        general.AgregarTable(Table, Colum, BodegasInicio);
         ResultSet resultado= Querys.InicioEmpresa();
         Sucursal.removeAll();
        general.AgregarDatosCombobox(Sucursal, resultado,1);
    }
    
