    private void btActualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btActualizarActionPerformed
      try {
            String Id = Cod.getText();
            String Estado =EstadoPro.getSelectedItem().toString();
            String Descripcions =Descripcion.getText();
            String DireccionBodega =DrBodegas.getText();
            String Ciudad =CodCiudad.getSelectedItem().toString();
            String Sucursals =Sucursal.getSelectedItem().toString();
            Querys.UpdateBodega( Estado, Descripcions, DireccionBodega, Ciudad, Sucursals, Id);
            InicioDatas(Querys.BodegasInicio());
        activa(false);
        btModificar.setEnabled(false);
        } catch (SQLException ex) {
            Logger.getLogger(CrearBodegas.class.getName()).log(Level.SEVERE, null, ex);
        }        // TODO add your handling code here:
    }//GEN-LAST:event_btActualizarActionPerformed

