    private void btGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btGuardarActionPerformed
        try {
            String Estado =EstadoPro.getSelectedItem().toString();
            String Descripcions =Descripcion.getText();
            String DireccionBodega =DrBodegas.getText();
            String Ciudad =CodCiudad.getSelectedItem().toString();
            String Sucursals =Sucursal.getSelectedItem().toString();
            
            Querys.InsertarBodegas(Estado,Descripcions,DireccionBodega,Ciudad,Sucursals );
            InicioDatas(Querys.BodegasInicio());
        activa(false);
        btModificar.setEnabled(false);
        } catch (SQLException ex) {
            Logger.getLogger(CrearBodegas.class.getName()).log(Level.SEVERE, null, ex);
        }

    }//GEN-LAST:event_btGuardarActionPerformed

    private void TableMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_TableMouseClicked
    btModificar.setEnabled(true);
    String [] DatosTable =general.DatosJTable(Table);
    Cod.setText(DatosTable[0]);
    Descripcion.setText(DatosTable[1]);
    DrBodegas.setText(DatosTable[2]);
    Sucursal.setSelectedItem(DatosTable[4]);
    CodCiudad.setSelectedItem(DatosTable[3]);  
    EstadoPro.setSelectedItem(DatosTable[5]);
    }//GEN-LAST:event_TableMouseClicked
        public void activa (Boolean x){
            Descripcion.setEnabled(x);
            DrBodegas.setEnabled(x);
            EstadoPro.setEnabled(x);
            Sucursal.setEnabled(x);
            CodCiudad.setEnabled(x);
            btCancelar.setVisible(x);
            btActualizar.setVisible(x);
            btModificar.setVisible(!x);
            btAdicionar.setVisible(!x);
            btGuardar.setEnabled(x);
        }
